export default {
    install(Vue) {
        // 全局过滤器
        Vue.filter('mySlice', function (val){
            return val.slice(0, 4);
        });
        // 全局指令
        Vue.directive('fbind', {
            bind(el, binding) {
                console.log('bind');
                el.vaule = binding.value;
            },
            inserted(el, binding) {
                console.log('inserted');
                el.focus();
            },
            update(el, binding) {
                console.log('update');
            }
        });
    }
}
