export default {
    install(Vue) {
        // 全局过滤器
        Vue.filter('mySlice', function (val){
            return val.slice(0, 4);
        });
        // 全局指令
        Vue.directive('fbind', {
            bind(el, binding) {
                console.log('bind');
                el.vaule = binding.value;
            },
            inserted(el, binding) {
                console.log('inserted');
                el.focus();
            },
            update(el, binding) {
                console.log('update');
                el.vaule = binding.value;
            }
        });
        // 全局混入
        Vue.mixin({
            data() {
                return {
                    x: 1,
                    y: 2
                }
            }
        });
        // 给 vue 原型上添加一个方法, vc/vm 都可以使用
        Vue.prototype.hello = function() {
            alert('hello')
        }
    }
}
